    def parse(self,respose):
        for href in respose.css('.SunChNewListing .col-sm-8 a::attr(href)'):
          url = href.extract()
          url = 'http://www.deccanchronicle.com' + url
          yield scrapy.Request(url, callback = self.parse_item)



    def parse_item(self, response):
        yield {
        'Tags': response.css('.articleTags a::text').extract(),
        'ContentParagraph': response.css('#storyBody p::text').extract(),
        'DateTime': response.css('.col-sm-5.noMargin::text').extract(),
        'author': response.css('.attribution a::text').extract(),
        'Headline': response.css('.headline span::text').extract()
        }
